import React, { Component } from 'react';

import { BrowserRouter as Router, Route } from 'react-router-dom';
class Admin extends Component {
  state = {
    userName: '',
    password: '',
    success:0
  };
  StyleForm = () => {
    return {
      padding: "100px 500px 0px",
    };
  };
  StyleButton = () => {
    return {
      margin: "0px 0px 0px",
      width:"535px"
    };
  };
 
  Login = () => {
    if(this.state.userName === this.props.userName && this.state.password === this.props.password){
      this.setState({success:1});
      this.render();
    }
    
  }
  render() {
    if(success===0){
    return (
      <React.Fragment>
        <form style={this.StyleForm()} action="javascript:void(0);" >
          <div className="form-group">
            <label for="userName">UserName:</label>
            <input
              type="text"
              className="form-control"
              id="email"
              onChange={(e) =>{
                console.log(e.target.value);
                console.log(this.state.userName);
                
                this.setState({ userName: e.target.value||"" })}
              }
            />
          </div>
          <div className="form-group">
            <label for="pwd">Password:</label>
            <input
              type="password"
              className="form-control"
              id="pwd"
              onChange={(e) =>
                this.setState({ password: e.target.value||"" })
              }
            />
          </div>               
          <button onClick={this.Login} style={this.StyleButton()} className="btn btn-default">Submit</button>     
        </form>
      </React.Fragment>
    );
  }
  else{
    return(
      <React.Fragment>

      </React.Fragment>
    );
  }
}
}

export default Admin;
