import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Blogs from './components/Blogs';
import Pagination from './components/Pagination';
import Admin from './components/pages/Admin'
import axios from 'axios';

import './App.css';

class App extends Component {
  state = {
    blogs: [],
    currentPage: 1,
    blogsPerPage: 10,
    totalBlogs: 0,
    loading: false,
  };
  componentDidMount() {
    this.getBlogs();
  }
  componentDidUpdate(prevProps, prevState) {
    if (this.state.currentPage != prevState.currentPage) this.getBlogs();
  }
  getBlogs = async () => {
    this.setState({ loading: true });
    console.log(this.state.currentPage);
    let res = await axios.get(
      'https://blogdatabase0101.herokuapp.com/page/' + this.state.currentPage
    );
    this.setState({
      blogs: res.data.blogs,
      totalBlogs: res.data.count,
      loading: false,
    });
  };
  paginate = (pageNumber) => {
    this.setState({
      currentPage: pageNumber,
    });
  };
  render() {
    return (
      <Router>
        <Route exact path='/'>
        <div className="container mt-5">
          <Blogs blogs={this.state.blogs} loading={this.state.loading} />
          <Pagination
            blogsPerPage={this.state.blogsPerPage}
            totalBlogs={this.state.totalBlogs}
            paginate={this.paginate}
          />
        </div>
        </Route>
        <Route path="/admin">
          <Admin/>
        </Route>
      </Router>
    );
  }
}

export default App;
