import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
class BlogData extends Component {
  state = {
    like: 1,
    comment:""
  };
  Like = () => {
    console.log('liking');
    if (this.state.like == 1) {
      this.setState({
        like: -1,
      });
    } else {
      this.setState({
        like: 1,
      });
    }
  };
  imgStyle = () => {
    return {
      minWidth: '480px',
      maxWidth: '900px'
    };
  };
  blogStyle = () => {
    return {
      margin: '10px',
      padding: '10px',
    };
  };
  render() {
    if (this.props.loading) {
      return <h2>Loading...</h2>;
    }
    const { title, body, imgUrl, comments, likes,created,lastUpdated } = this.props.blog;
    return (
      <div className="container-fluid" style={this.blogStyle()}>
        <div className="container-fluid" style={this.blogStyle()}>
          <h1 className="display-4" style={{textAlign:'center'}}>{title}</h1>

          <div style={{
          display:'flex',
      justifyContent: 'center',
      alignItems: 'center'}}>
        <img
          src={imgUrl}
          style={this.imgStyle()}
          alt="No Pic"
        /></div>
          <pre style={{fontWeight:'lighter',fontSize:'30px',paddingLeft:'30px',paddingRight:'30px',maxWidth:'80%',
overflowWrap: 'break-word',overflow: 'ellipsis',whiteSpace: 'pre-wrap'  }}>{body}</pre>
          <div>
            <button
              onClick={() => {
                this.props.setLike(this.state.like);
                this.Like();
              }}>
              {this.state.like == 1 ? (
                <i class="far fa-thumbs-up"></i>
              ) : (
                <i class="fas fa-thumbs-up"></i>
              )}
              {likes}
            </button>
            <p>Created on:{created}</p>
          </div>
        </div>
        <div>
          Comments:
          {comments.map((number,i) => (
            <div>
            <p>{number}</p>
            <button style={{display:'inline'}} onClick={this.props.comment.bind()}><i class="fas fa-trash"></i></button>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

export default BlogData;
