import React, { Component } from 'react';
import axios from 'axios';
class BlogData extends Component{

    state={
        blogs:{}
    }
    componentDidMount(){      
    this.getBlogs();
    };
    
  getBlogs = async () => {
    this.setState({ loading: true });
    let res = await axios.get(
      'https://blogdatabase0101.herokuapp.com/page/' + this.props.id
    );
    this.setState({
      blog: res.data
    });
  };
    imgStyle = () =>{
      return{
      width: "480px",
      height: "480px"
      }
    }
    blogStyle = () =>{
      return{
      backgroundColor: "#d9d9d9",
      margin: "10px",
      padding: "10px",
      border:"1px solid black"
      }
    }
    render() {
      const { title, body, imgUrl,comments,likes } = this.state.blog;
      return (
        <div className="container-fluid" style={this.blogStyle()}>
          <h1 className="display-4">
            {title}
          </h1>
          
          <img src={imgUrl} style={this.imgStyle()} alt="No Pic"/>
          <p>
            {body}
          </p>
          <div>
              Comments:
          {comments.map((number) => (
            <p>{number}</p>
          ))}
          </div>
          <div>
              Likes:
              <p></p>
          </div>
        </div>
      );
    }
  }
  
  export default BlogData;
