import React, { Component } from 'react';
import axios from 'axios';
class BlogData extends Component {
  state = {
    blog: {},
    loading: false,
  };

  getBlogs = async () => {
    this.setState({ loading: true });
    let res = await axios.get(
      'https://blogdatabase0101.herokuapp.com/' + this.props.id
    );
    this.setState({
      blog: res.data,
      loading: false,
    });
  };
  imgStyle = () => {
    return {
      width: '480px',
      height: '480px',
    };
  };
  blogStyle = () => {
    return {
      backgroundColor: '#d9d9d9',
      margin: '10px',
      padding: '10px',
      border: '1px solid black',
    };
  };
  render() {
    if (this.state.loading) {
      return <h2>Loading...</h2>;
    } else {
      console.log('B', this.state.blog);
      return (
       <h2>Loading...</h2>
      );
    }
  }
}

export default BlogData;
