import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Blogs from './AdminBlogs/Blog';
import Pagination from './AdminBlogs/Pagination';
import BlogData from './AdminBlogs/BlogData';
import axios from 'axios';
class DashBoard extends Component{    

    state = {
        blogs: [],
        currentPage: 1,
        blogsPerPage: 10,
        totalBlogs: 0,
        loading: false,
        loadingBlog:false,
        userName: '',
        password: '',
        blogId: 0,
      };
      componentDidMount() {
        console.log(
          
        )
        this.getBlogs();
      }
      componentDidUpdate(prevProps, prevState) {    
        if (this.state.currentPage != prevState.currentPage) this.getBlogs();
      }
      getBlogs = async () => {
        this.setState({ loading: true });
        let res = await axios.get(
          'https://blogdatabase0101.herokuapp.com/page/' + this.state.currentPage
        );
        this.setState({
          blogs: res.data.blogs,
          totalBlogs: res.data.count,
          loading: false,
        });
      };
      getBlog = async (id) => {
        this.setState({ loadingBlog: true });
        let res = await axios.get(
          'https://blogdatabase0101.herokuapp.com/' + id
        );
        console.log("LLikes=",res.data.likes)
        this.setState({
          blog: res.data,
          loadingBlog: false,
        });
      };
      paginate = (pageNumber) => {
        this.setState({
          currentPage: pageNumber,
        });
      };
      setId = (id) => {
        this.setState({blogId:id})
        this.getBlog(id);
      };
      comment = (comment) => {
        console.log('commented')
        console.log(comment)
        axios.put('https://blogdatabase0101.herokuapp.com/' + this.state.blogId,[{"propName":"comments","value":{"value":""+comment}}]).then((res)=>{this.getBlog(this.state.blogId)});
      }
      render() {
        return (
          <Router>
            <button className="btn btn-primary btn-block" style={{textAlign:"center"}}>Create a new Blog</button>
            <Route exact path="/admin/">
              <div className="container mt-5">
                <Blogs
                  blogs={this.state.blogs}
                  loading={this.state.loading}
                  blogId={this.setId}
                />
                <Pagination
                  blogsPerPage={this.state.blogsPerPage}
                  totalBlogs={this.state.totalBlogs}
                  paginate={this.paginate}
                />
              </div>
            </Route>
            <Route exact path="/admin/#">
              <div className="container mt-5">
                <Blogs
                  blogs={this.state.blogs}
                  loading={this.state.loading}
                  blogId={this.setId}
                />
                <Pagination
                  blogsPerPage={this.state.blogsPerPage}
                  totalBlogs={this.state.totalBlogs}
                  paginate={this.paginate}
                />
              </div>
            </Route>
            <Route path="/admin/blogData">
              <BlogData 
                  loading={this.state.loadingBlog}
                  blog={this.state.blog}
                  comment={this.comment}/>
            </Route>
          </Router>
        );
      }
}

export default DashBoard;
