import React, { Component } from 'react'
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Blogs from './components/Blogs';
import Pagination from './components/Pagination';
import uuid from 'uuid';
import axios from 'axios';

import './App.css';

class App extends Component {
  state = {
    blogs: [],
    currentPage :1,
    blogsPerPage : 10,
    totalBlogs:0,
    loading:false
  }
  
  componentDidMount() {
    
  }
  paginate = pageNumber => this.setState({
    currentPage: pageNumber
  });
  render() {
    const fetchPosts = async () => {
      this.setState({loading:true})
      const res = await axios.get('https://blogdatabase0101.herokuapp.com/page/'+this.state.currentPage);
      this.setState({ blogs: res.data.blogs,totalBlogs: res.data.count ,loading:false})
    };

    fetchPosts();
    return (
      <div className='container mt-5'>
      <Blogs blogs={this.state.blogs} />
      <Pagination
        blogsPerPage={this.state.blogsPerPage}
        totalBlogs={this.state.totalBlogs}
        paginate={this.paginate}
      />
    </div>
    );
  }
}

export default App;
