import React, { Component } from 'react';
class BlogData extends Component{

    state={
        blogs:[]
    }
    componentDidMount(){      
    this.getBlogs();
    };
    
  getBlogs = async () => {
    this.setState({ loading: true });
    let res = await axios.get(
      'https://blogdatabase0101.herokuapp.com/page/' + this.state.currentPage
    );
    this.setState({
      blogs: res.data.blogs,
      totalBlogs: res.data.count,
      loading: false,
    });
  };
    imgStyle = () =>{
      return{
      width: "480px",
      height: "480px"
      }
    }
    blogStyle = () =>{
      return{
      backgroundColor: "#d9d9d9",
      margin: "10px",
      padding: "10px",
      border:"1px solid black"
      }
    }
    render() {
      const { _id, title, body, imgUrl } = this.state.blog;
      console.log("id=",_id)
      return (
        <div className="container-fluid" style={this.blogStyle()}>
          <h1 className="display-4">
            {title}
          </h1>
          
          <img src={imgUrl} style={this.imgStyle()} alt="No Pic"/>
          <p>
            {body}...
          </p>

        </div>
      );
    }
  }
  
  export default BlogData;
