import React, { Component } from 'react';
import axios from 'axios';
class BlogData extends Component {
  state = {
    like: 1,
  };
  Like = () => {
    console.log('liking');
    if (this.state.like == 1) {
      this.setState({
        like: -1,
      });
    } else {
      this.setState({
        like: 1,
      });
    }
  };
  imgStyle = () => {
    return {
      width: '480px',
      height: '480px',
    };
  };
  blogStyle = () => {
    return {
      margin: '10px',
      padding: '10px',
      justifyContent:'center'
    };
  };
  render() {
    if (this.props.loading) {
      return <h2>Loading...</h2>;
    }
    const { title, body, imgUrl, comments, likes } = this.props.blog;
    return (
      <div className="container-fluid" style={this.blogStyle()}>
        <div className="container-fluid" style={this.blogStyle()}>
          <h1 className="display-4" style={{justifyContent:'center'}}>{title}</h1>

          <img src={imgUrl} style={this.imgStyle()} alt="No Pic" />
          <p>{body}</p>
          <div>
            <button
              onClick={() => {
                this.props.setLike(this.state.like);
                this.Like();
              }}>
              {this.state.like == 1 ? (
                <i class="far fa-thumbs-up"></i>
              ) : (
                <i class="fas fa-thumbs-up"></i>
              )}
              {likes}
            </button>
          </div>
        </div>
        <div>
          Comments:
          <imput type='text'></imput>
          {comments.map((number) => (
            <p>{number}</p>
          ))}
        </div>
      </div>
    );
  }
}

export default BlogData;
