import React, { Component } from 'react';
import axios from 'axios';
class BlogData extends Component {
  state = {
    like: 1,
    comment:""
  };
  Like = () => {
    console.log('liking');
    if (this.state.like == 1) {
      this.setState({
        like: -1,
      });
    } else {
      this.setState({
        like: 1,
      });
    }
  };
  imgStyle = () => {
    return {
      minWidth: '480px',
      maxWidth: '900px'
    };
  };
  blogStyle = () => {
    return {
      margin: '10px',
      padding: '10px',
    };
  };
  render() {
    if (this.props.loading) {
      return <h2>Loading...</h2>;
    }
    const { title, body, imgUrl, comments, likes } = this.props.blog;
    return (
      <div className="container-fluid" style={this.blogStyle()}>
        <div className="container-fluid" style={this.blogStyle()}>
          <h1 className="display-4" style={{textAlign:'center'}}>{title}</h1>

          <div style={{
          display:'flex',
      justifyContent: 'center',
      alignItems: 'center'}}>
        <img
          src={imgUrl}
          style={this.imgStyle()}
          alt="No Pic"
        /></div>
          <p style={{fontWeight:'lighter',fontSize:'30px',paddingLeft:'30px',paddingRight:'30px'}}>{body}</p>
          <div>
            <button
              onClick={() => {
                this.props.setLike(this.state.like);
                this.Like();
              }}>
              {this.state.like == 1 ? (
                <i class="far fa-thumbs-up"></i>
              ) : (
                <i class="fas fa-thumbs-up"></i>
              )}
              {likes}
            </button>
          </div>
        </div>
        <div>
          <form style={{width:'100%'}} action="javascript:void(0);" >
          <div className="form-group">
            <label for="userName" style={{paddingRight:"90%"}}>Enter a Comment:</label>
            <textarea name="Text1" cols="100" rows="5" placeholder="Enter comment here...." onChange={e=>this.setState({comment: e.target.value})}></textarea>
          </div>  
          
          <button onClick={this.props.comment.bind(this,this.state.comment)}  className="btn btn-default">Submit</button>  
        </form>
          Comments:
          {comments.map((number) => (
            <p>{number}</p>
          ))}
        </div>
      </div>
    );
  }
}

export default BlogData;
